// const Page = props => (
//   <Layout>
//     <Content />
//   </Layout>
// );

const Post = props => (
  <Layout>
    <h1>Post</h1>
  </Layout>
)

Post.getInitialProps = async function(context) {
  const { id } = context.query
  const res = await fetch(`https://jsonplaceholder.typicode.com/posts/${id}`)
  const data = await res.json()

  return {
    post: data
  }
}

export default Post;
